package test1;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//implict conversion
		System.out.println("implict conversion");
		char a = 'A';
		System.out.println(" before conversion a =" +a);
		
		int b = a;
		System.out.println(" after conversion a =" +b);
		
		//explict conversion
				System.out.println("explict conversion");
				double c = 67.7;
				System.out.println(" before conversion a =" +c);
				
				int d = (int)c;
				System.out.println(" after conversion a =" +d);
		
		

	}

}
